import styled from "styled-components"

const Carta = styled.div `
   background-color: white;
   color: black;
   font-size: 0.7em;
   padding-top: 1em;
   border-radius: 3em;
   height: 40em;
   margin: 1em;
   font-family: 'Gill Sans', 'Gill Sans MT', Calibri, 'Trebuchet MS', sans-serif;
`;
const Nombre = styled.h2 `
   font-size: 2em;
`;
const Boton = styled.button `
   margin-left: 11em;
   background-color: white;
   border-color: white;
   border-radius: 1em;
   border-width: 0em;
   font-family: 'Gill Sans', 'Gill Sans MT', Calibri, 'Trebuchet MS', sans-serif;
   font-size: 2em;
   color: black;
`;
const Imagen = styled.img `
   border-radius: 3em;
`

export default function Card({onClose, name, status, species, gender, origin, image}) {
   return (
     <Carta>
         <Boton onClick={onClose}>X</Boton>     
         <Nombre>{name}</Nombre>
         <h2>{status}</h2>
         <h2>{species}</h2>
         <h2>{gender}</h2>
         <h2>{origin.name}</h2>
         <Imagen src={image} alt={name}/>
     </Carta>

   );
}
