import styled from "styled-components";
import axios from "axios"
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const Container = styled.div `
  display: flex;
  justify-content: center;
`

const Cartita = styled.div `
    height: 18em;
    width: 40em;
    display: flex;
    box-shadow: 0 0 1em 0 black;
    background-color: white;
    margin-left: 2em;
    margin-right: 30em;
    margin-top: 3em;
    padding: 2em;
    border-radius: 2em;
    border-style: solid;
    border-width: 2px;
    border-color: black;
    font-family: 'Gill Sans', 'Gill Sans MT', Calibri, 'Trebuchet MS', sans-serif;
    font-size: 2em;
`
const DivA = styled.div `
    display: flex;
    flex-direction: column;
    margin-right: 10em;
`
const DivB = styled.div `
    display: flex;
    flex-direction: column;
`
const Img = styled.img `
    width: 15em;
    height: 15em;
    border-radius: 1em;
`

export const Detail = () => {
  const { id } = useParams()
  const [charDetail, setCharDetail] = useState({})

  useEffect(()=> {
    axios(`https://rickandmortyapi.com/api/character/${id}`).then(({data}) => {
      if(data.name) {
        setCharDetail(data);
      } else {
        window.alert('No hay personajes con ese ID')
      }
    })
    return setCharDetail({})
  }, [id])

  return (
    <Container>
    <Cartita>
      <DivA>
        <Img src={charDetail.image}/>
        <h2>{charDetail.name}</h2>
      </DivA>
      <DivB>
        <h4>Specie: {charDetail.species}</h4>
        <h4>Gender: {charDetail.gender}</h4>
        <h4>Status: {charDetail.status}</h4>
        <h4>Origin: {charDetail.origin?.name}</h4>
      </DivB>
    </Cartita>
    </Container>
  )
}

export default Detail;
