import styled from "styled-components"


const Carta = styled.div `
   background-color: #ffffff;
   color: black;
   font-size: 0.7em;
   border-radius: 3em;
   height: 35em;
   margin: 2em;
   font-family: 'Gill Sans', 'Gill Sans MT', Calibri, 'Trebuchet MS', sans-serif;
`;
const Nombre = styled.h2 `
   position: absolute;
   top: 77%;
   left: 5%;
   font-size: 3em;
   z-index: 2;
`;
const Boton = styled.button `
   position: absolute;
   top: 3%;
   right: 3%;
   margin-left: 11em;
   background-color: white;
   border-color: transparent;
   border-radius: 2em;
   border-width: 0em;
   font-family: 'Gill Sans', 'Gill Sans MT', Calibri, 'Trebuchet MS', sans-serif;
   font-size: 2em;
   color: black;
   cursor: pointer;
   z-index: 2;
`;
const Imagen = styled.img `
   border-radius: 2em;
   top: 100%;
   z-index: 1;
   padding-bottom: 2em;
`;
const Info = styled.div `
   display: flex;
   justify-content: space-evenly;
`;
const IBN = styled.div `
   display: inline-block;
   position: relative;
   padding: -3em;
`

export default function Card({onClose, name, status, species, gender, origin, image}) {
   return (
     <Carta>
         <IBN>
           <Boton onClick={onClose}>X</Boton>
           <Imagen src={image} alt={name}/>
           <Nombre>{name}</Nombre>
         </IBN>
         
         <Info>
           <h2>{species}</h2>
           <h2>{gender}</h2>
           <h2>{status}</h2>
         </Info>
         <h2>{origin.name}</h2>
         
     </Carta>

   );
}
