import styled from "styled-components";
import axios from "axios"
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";


const Cartita = styled.div `
    box-shadow: 0 0 1em 0 black;
    background-color: white;
    margin-left: 2em;
    margin-right: 30em;
    margin-top: 3em;
    padding: 5em;
    border-radius: 2em;
    border-style: solid;
    border-width: 2px;
    border-color: black;
    font-family: 'Gill Sans', 'Gill Sans MT', Calibri, 'Trebuchet MS', sans-serif;
    font-size: 2em;
`
export const Detail = () => {
  const { id } = useParams()
  const [charDetail, setCharDetail] = useState({})

  useEffect(()=> {
    axios(`https://rickandmortyapi.com/api/character/${id}`).then(({data}) => {
      if(data.name) {
        setCharDetail(data);
      } else {
        window.alert('No hay personajes con ese ID')
      }
    })
    return setCharDetail({})
  }, [id])

  return (
    <Cartita>
      <img src={charDetail.image}/>
      <h2>{charDetail.name}</h2>
      <h4>Specie: {charDetail.species}</h4>
      <h4>Gender: {charDetail.gender}</h4>
      <h4>Status: {charDetail.status}</h4>
      <h4>Origin: {charDetail.origin}</h4>
    </Cartita>
  )
}

export default Detail;
