import { useState } from "react"
import { validation } from "./Validation";
import styled from "styled-components";

const Container = styled.div `
    display: flex;
    flex-direction: column;
    align-items: center;
    padding-top: 4em;
`
const Carta = styled.div `
    display: flex;
    justify-content: center;
    flex-wrap: wrap;
    align-items: center;
    height: 20em;
    width: 30em;
    border-radius: 2em;
    border-style: solid;
    border-width: 2px;
    border-color: black;
    background-color: #fff1d8;
    padding-top: 2em;
`
const Email = styled.div `
    height: 5em;
    width: 25em;
    font-family: 'Gill Sans', 'Gill Sans MT', Calibri, 'Trebuchet MS', sans-serif;
    font-size: 1.5em;
`
const Password = styled.div `
    height: 5em;
    width: 25em;
    font-family: 'Gill Sans', 'Gill Sans MT', Calibri, 'Trebuchet MS', sans-serif;
    font-size: 1.5em;
`
const Input = styled.input `
    margin-left: 1em;
    border-radius: 0.5em;
    border-width: 2px;
    border-style: solid;
    border-color: black;
    height: 2em;
    width: 15em;
    padding-left: 1em;
`

const Danger = styled.h1 `
    font-size: 12px;
    color: red;
    margin-top: 0.2em;
    background-color: beige;
    padding: 0.5em;
    border-radius: 0.3em;
    margin-left: 2em;
    margin-right: 2em;
`


export default function Form({login}) {
    const [userData, setUserData] = useState({
        email:'',
        password:'',
    });

    const [errors, setErrors] = useState({})

    function handleChange(event){
        setErrors(validation({...userData, 
            [event.target.name]: event.target.value}))
        setUserData({...userData, 
            [event.target.name]: event.target.value})
    }
    function handleSubmit(event){
        event.preventDefault()
        login(userData)
    }

    
  return (
    <Container>
        <Carta>
            <Titulo>Bienvenido a la app de Rick And Morty</Titulo>
            <Cabecera>Por favor, inicie sesi√≥n para poder ingresar a la app</Cabecera>           
       <form onSubmit={handleSubmit}> 
            <Email>
            <label htmlFor="email">
                Email:
                <Input 
                    type="text" 
                    id="email" 
                    value={userData.email}
                    name="email"
                    onChange={handleChange}/>
            </label>
            {errors.email && (<Danger>{errors.email}</Danger>)}
            </Email>
            <Password>
            <label htmlFor="password">
                Password:
                <Input 
                    type="password" 
                    id="password" 
                    value={userData.password}
                    name="password"
                    onChange={handleChange}/>
            </label> 
            {errors.password && (<Danger>{errors.password}</Danger>)}
            </Password>

            <button>Submit</button>

       </form>
       </Carta>
    </Container>
  )
}
