import styled from "styled-components"
import { useState } from "react";


const Fondo = styled.div `
   box-shadow: 0 0 0.5em 0 black; 
   display: flex;
   justify-content: space-between;
   background-color: #e8d2aa;
   border-style: solid;
   border-width: 2px;
   border-top-width: 0em;
   border-color: #000000;
   padding-top: 0em;
   padding-right: 0.5em;
   padding-bottom: 1em;
   border-bottom-left-radius: 3em;
   border-bottom-right-radius: 3em;
   margin-left: 3.5em;
   margin-right: 3.5em;
`;

const Cabecera = styled.div `
   justify-content: start;
   padding-left: 4em;
   padding-top: 1em;
`

const Titulo = styled.div `
   color: black;
   font-family: Schwifty;
   font-size: 4em;
`
const By = styled.h1 `
   padding-top: 3.3em;
   color: black;
   font-size: 1.1em;
   font-family: 'Gill Sans', 'Gill Sans MT', Calibri, 'Trebuchet MS', sans-serif;
`

const Barra = styled.div `
   padding-left: 1em;
`;
const Busqueda = styled.input `
   border-style: solid;
   border-color: #000000;
   height: 4em;
   border-radius: 3em;
   margin-right: 1em;
   padding-left: 2em;
   padding-right: 2em;
   &:hover {
      background-color: #fff1d8;
   };
`;
const Agregar = styled.button `
   border-style: solid;
   border-color: #000000;
   background-color: grey;
   height: 3.2em;
   padding: 0.5em;
   margin-right: 1em;
   border-radius: 3em;
   font-family: 'Gill Sans', 'Gill Sans MT', Calibri, 'Trebuchet MS', sans-serif;
   font-size: 1em;
   cursor: pointer;
   color: white;
   &:hover {
      background-color: #e8e1d8;
      color: grey;
   };
`
const Aleatorio = styled.button `
   border-style: solid;
   border-color: #000000;
   background-color: #873737;
   height: 3.2em;
   padding: 0.5em;
   margin-right: 1em;
   border-radius: 3em;
   font-family: 'Gill Sans', 'Gill Sans MT', Calibri, 'Trebuchet MS', sans-serif;
   font-size: 1em;
   cursor: pointer;
   color: white;
   &:hover {
      background-color: #dababa;
      color: #873737;
   };
`

const Texto = styled.h1 `
   color: #954444;
   font-size: 0.9em;
`

const Space = styled.h1 `
   width: 22em;
`

const randomNum = () => {
   var result = Math.floor(Math.random()*826);
   return result;
}

var random = randomNum();

export default function SearchBar({onSearch}) {
   const [id, setId] = useState('');

   function handleChange(event){
      setId(event.target.value);
   }

   return (
      <Fondo>
         
            <Texto>Pista: hay 826 personajes! ...solo pod√©s elegir un aleatorio ;)
            </Texto>
         <Busqueda type='search' onChange={handleChange}/>
         <Agregar onClick={()=> onSearch(id)}>Agregar</Agregar> 
         <Aleatorio onClick={()=> onSearch(random)}>Aleatorio</Aleatorio>
      </Fondo>
   );
}
