    box-shadow: 0 0 1em 0 black;
    background-color: white;
    margin-left: 2em;
    margin-right: 30em;
    margin-top: 3em;
    padding: 5em;
    border-radius: 2em;
    border-style: solid;
    border-width: 2px;
    border-color: black;
    font-family: 'Gill Sans', 'Gill Sans MT', Calibri, 'Trebuchet MS', sans-serif;
    font-size: 2em;
`
export const Detail = () => {
  const { id } = useParams()
  const [charDetail, setCharDetail] = useState({})

  useEffect(()=>{
    const dataDetail = data.filter(char => char.id === id)
    if (dataDetail){
      setCharDetail(dataDetail)
    }
  }, [])

  return (
    <Cartita>
      Detalles del personaje
    </Cartita>
  )
}

export default Detail;
