import styled from "styled-components"

const Carta = styled.div `
   background-color: #ffffff;
   color: black;
   font-size: 0.7em;
   padding-top: 1em;
   border-radius: 3em;
   height: 50em;
   margin: 1em;
   font-family: 'Gill Sans', 'Gill Sans MT', Calibri, 'Trebuchet MS', sans-serif;
`;
const Nombre = styled.h2 `
   font-size: 2em;
   padding-top: -2em;
`;
const Boton = styled.button `
   margin-left: 11em;
   background-color: white;
   border-color: white;
   border-radius: 1em;
   border-width: 0em;
   font-family: 'Gill Sans', 'Gill Sans MT', Calibri, 'Trebuchet MS', sans-serif;
   font-size: 2em;
   color: black;
   cursor: pointer;
`;
const Imagen = styled.img `
   border-radius: 0em;
`
const Info = styled.div `
   justify-content: flex;
`

export default function Card({onClose, name, status, species, gender, origin, image}) {
   return (
     <Carta>
         <Boton onClick={onClose}>X</Boton>
         <Imagen src={image} alt={name}/>
         <Nombre>{name}</Nombre>
         <Info>
           <h2>{species}</h2>
           <h2>{gender}</h2>
           <h2>{status}</h2>
         </Info>
         <h2>{origin.name}</h2>
         
     </Carta>

   );
}
