import styled from "styled-components"
import onSearch from "../App.js"

const Fondo = styled.div `
   box-shadow: 0 0 0.5em 0 black; 
   display: flex;
   justify-content: end;
   background-color: #e8d2aa;
   border-style: solid;
   border-width: 2px;
   border-color: #000000;
   padding-top: 1em;
   padding-right: 4em;
   padding-bottom: 1em;
   border-bottom-left-radius: 3em;
   border-bottom-right-radius: 3em;
   margin-left: 3.5em;
   margin-right: 3.5em;
`;
const Barra = styled.div `
   padding-left: 1em;
`;
const Busqueda = styled.input `
   border-style: solid;
   border-color: #000000;
   height: 4em;
   border-radius: 3em;
   margin-right: 1em;
   padding-left: 2em;
   padding-right: 2em;
   &:hover {
      scale: 1.05;
   };
`;
const Lupa = styled.button `
   border-style: solid;
   border-color: #000000;
   background-color: grey;
   height: 3.2em;
   padding: 0.5em;
   border-radius: 3em;
   font-family: 'Gill Sans', 'Gill Sans MT', Calibri, 'Trebuchet MS', sans-serif;
   font-size: 1em;
   cursor: pointer;
   color: white;
   &:hover {
      scale: 1.05;
   };
`

