import styled from "styled-components"

const Carta = styled.div `
   background-image: src("C:\Users\mateo\Desktop\Curso Henry\ryck_and_morty\src\components\imagenes\portalgif.gif");
   color: black;
   font-size: 1em;
   padding-top: 2em;
   border-radius: 2em;
   height: 40em;
   margin: 1em;
`;
const Nombre = styled.h2 `
   font-size: 2em;
`;
const Boton = styled.button `
   align-items: flex-end;
`

export default function Card({onClose, name, status, species, gender, origin, image}) {
   return (
      <div>
         <Carta>
           <Boton onClick={onClose}>X</Boton>
           <Nombre>{name}</Nombre>
           <h2>{status}</h2>
           <h2>{species}</h2>
           <h2>{gender}</h2>
           <h2>{origin.name}</h2>
         </Carta>
         <div>
           <img src={image} alt={name}/>
         </div>
      </div>
   );
}
