import styled from "styled-components"

const Fondo = styled.div `
   display: flex;
   justify-content: end;
   background-color: white;
   border-style: groove;
   border-color: #45a8ff;
   padding-top: 1em;
   padding-right: 4em;
   padding-bottom: 1em;
   border-bottom-left-radius: 3em;
   border-bottom-right-radius: 3em;
   margin-left: 4em;
   margin-right: 4em;
`;
const Barra = styled.div `
   padding-left: 1em;
`;
const Busqueda = styled.input `
   height: 4em;
   border-radius: 3em;
   margin-right: 1em;
   padding-left: 2em;
   padding-right: 2em;
`;
const Lupa = styled.button `
   background-color: grey;
   height: 3.2em;
   padding: 0.5em;
   border-radius: 3em;
   font-family: 'Gill Sans', 'Gill Sans MT', Calibri, 'Trebuchet MS', sans-serif;
   font-size: 1em;
   cursor: pointer;
   color: white;
`

export default function SearchBar({onSearch}) {
   return (
      <Fondo>  
         <Barra>
         <Busqueda type='search' />
         <Lupa onClick={onSearch}>Agregar</Lupa>     
         </Barra>      
      </Fondo>
   );
}
