import styled from "styled-components"

const Fondo = styled.div `
   display: flex;
   padding-bottom: 0.5em;
   border-color: black;
`;
const Barra = styled.div `
   padding-left: 0.5em;
   padding-top: 0.5em;
`;
const Busqueda = styled.input `
   height: 3em;
   border-radius: 3em;
`;
const Lupa = styled.button `
padding-top: 0.2em;
   background-color: white;
   height: 2.4em;
   border-color: white;
   border-radius: 3em;
   font-family: 'Gill Sans', 'Gill Sans MT', Calibri, 'Trebuchet MS', sans-serif;
   font-size: 1em;
`

export default function SearchBar({onSearch}) {
   return (
      <Fondo>  
         <Barra>
         <Busqueda type='search' />
         <Lupa onClick={onSearch}>Buscar</Lupa>     
         </Barra>      
      </Fondo>
   );
}
